mapper.insert:
        INSERT INTO COMTNMENUINFONEW 
			( me_code
            , me_name
            , me_link
            , me_target
            , me_order
            , me_use
            , me_place
            )
			VALUES (   
				#{meCode}
              , #{meName}
              , #{meLink}  
              , #{meTarget}
              , #{meOrder}
              , #{meUse}
              , #{mePlace}
            )

mapper.update:

        UPDATE COMTNMENUINFONEW 
			SET  me_name         = #{meName}       
            , me_link           = #{meLink}         
            , me_target     = #{meTarget}    
            , me_order = #{meOrder}
            , me_use   = #{meUse}
            , me_place   = #{mePlace}    
            WHERE me_id=#{meId}

mapper.insert:

			INSERT INTO COMTNMENUINFO 
				( MENU_NO
            , MENU_ORDR
            , MENU_NM
            , UPPER_MENU_NO
            , MENU_DC
            , RELATE_IMAGE_PATH
            , RELATE_IMAGE_NM
            , PROGRM_FILE_NM
            , LINK_URL)
			VALUES (   #{menuNo}
                  , #{menuOrdr}
                  , #{menuNm}  
                  , #{upperMenuId}
                  , #{menuDc}
                  , #{relateImagePath}
                  , #{relateImageNm}
                  , #{progrmFileNm} 
                  , #{linkUrl})

mapper.update:

			UPDATE COMTNMENUINFO 
			SET  MENU_ORDR         = #{menuOrdr}       
            , MENU_NM           = #{menuNm}         
            , UPPER_MENU_NO     = #{upperMenuId}    
            , MENU_DC           = #{menuDc}         
            , RELATE_IMAGE_PATH = #{relateImagePath}
            , RELATE_IMAGE_NM   = #{relateImageNm}  
            , PROGRM_FILE_NM    = #{progrmFileNm} 
            , LINK_URL          = #{linkUrl}
			WHERE MENU_NO=#{menuNo}

mapper.select:

SELECT COUNT(MENU_NO) AS totcnt
  FROM COMTNMENUINFO
 WHERE MENU_NO = #{menuNo}


SELECT COUNT(MENU_NO) AS totcnt
  FROM COMTNMENUINFO
 WHERE UPPER_MENU_NO = #{menuNo}
